import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-user-login',
  templateUrl: './user-login.component.html',
  styleUrls: ['./user-login.component.css']
})
export class UserLoginComponent {

  constructor(
    private router: Router,
    private authService: AuthService
    ){}
              //private alertify: AlertyfyService) {}

  ngOnInit(): void {
  }

  onLogin(loginForm: NgForm){
    console.log(loginForm.value);
    const token = this.authService.authUser(loginForm.value);
    if(token) {
      localStorage.setItem('token', token.userName);
      //this.alertify.success('Login Successfully');
      this.router.navigate(['../AddPatient']);
      //alert('Login Successfully');
    } else {
      //this.alertify.error('User id or passwird is wrong');
      alert('User id or passwird is wrong');
    }
  }
}
<div class="row">
  <div class="col-6 m-auto">
    <div class="card m-auto">
      <div class="card-header">
          Login
      </div>
      <div class="card-body">
        <form #loginForm='ngForm' (ngSubmit)="onLogin(loginForm)">
          <div class="form-group col-12">
            <label>Name</label>
            <input class="form-control" required ngModel name="userName">
          </div>

          <div class="form-group col-12">
            <label>Password</label>
            <input type="password" class="form-control" required ngModel name="password">
          </div>
          <br>
          <div class="form-group col-12">
            <button [disabled]="!loginForm.valid" type="submit" class="btn btn-primary">Login</button>&nbsp;
            <button type="reset" class="btn btn-secondary ml-2">Cancel</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>>
