import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { User } from 'src/app/model/user';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-user-register',
  templateUrl: './user-register.component.html',
  styleUrls: ['./user-register.component.css']
})
export class UserRegisterComponent implements OnInit {

  registrationForm!: FormGroup;

  /* Local Storage */
  user!: User;
  userSubmitted: boolean | undefined;
  constructor(private fb: FormBuilder,
              private userService: UserService){}

  ngOnInit() {
  /* Validation using FormControl */
  //  this.registrationForm = new FormGroup({
  //   userName : new FormControl ("",Validators.required),
  //   email : new FormControl(null, [Validators.required, Validators.email]),
  //   password : new FormControl(null, [Validators.required, Validators.minLength(8)]),
  //   confirmPassword : new FormControl(null, [Validators.required]),
  //   mobile : new FormControl(null, [Validators.required,Validators.minLength(10), Validators.maxLength(10)]),
  //  });
  this.createRegistraionForm();
  }

  /*Validation using FormBuilder */
  createRegistraionForm() {
    this.registrationForm = this.fb.group({
      userName: [null, Validators.required],
      email : [null, [Validators.required, Validators.email]],
      password : [null, [Validators.required, Validators.minLength(8)]],
      confirmPassword :[null, Validators.required],
      mobile : [null, [Validators.required, Validators.maxLength(10)]],
    }, {Validators: this.passwordMatchingValidator});
  }

  private passwordMatchingValidator(fg: FormGroup) {
    return fg.get('password')?.value === fg.get('confirmPassword')?.value ? false :
    {notmatched : true};
  }

  get userName(){
    return this.registrationForm.get('userName') as FormControl;
  }
  get email(){
    return this.registrationForm.get('email') as FormControl;
  }

  get password(){
    return this.registrationForm.get('password') as FormControl;
  }

  get confirmPassword(){
    return this.registrationForm.get('confirmPassword') as FormControl;
  }

  get mobile(){
    return this.registrationForm.get('mobile') as FormControl;
  }

  onSubmit(){
    console.log(this.registrationForm.value);
    this.userSubmitted = true;
    /* Values stored in Local storage */
    if(this.registrationForm.valid){
    //this.user = Object.assign(this.user, this.registrationForm.value);  // save data in localstorage using object
    this.userService.addUSer(this.userData())
    this.registrationForm.reset();
    this.userSubmitted = false;
    //this.alertyfyService.success("User is saved successfully");
    }
    else {
      //this.alertyfyService.error("Kindly provide required field");
    }
  }

  userData(): User {
    return this.user = {
      userName: this.userName.value,
      email: this.email.value,
      password: this.password.value,
      mobile: this.mobile.value
    }
  }


}
<div class="row">
  <div class="col-6 m-auto">
  <div class="card m-auto">
    <div class="card-header">
      Register
    </div>
    <div class="card-body">
      <form [formGroup]="registrationForm" (ngSubmit)="onSubmit()">
        <div class="form-group col-12">
            <label for="name" class="form-label">Name</label>
            <input type="text" class="form-control" formControlName="userName">
            <span *ngIf="!userName?.valid && (userName?.touched || userSubmitted)" class="error-block">Please provide Name</span>
        </div>

        <div class="form-group col-12">
          <label for="email" class="form-label">Email</label>
          <input type="text" class="form-control" formControlName="email" >
          <span *ngIf="!email.valid && (email.touched || userSubmitted)" class="error-block">
            <span *ngif="email?.hasError('required')">
              Please provide Email id
            </span>
            <span *ngif="email?.hasError('email')">
              Please provide valid Email id
            </span>
          </span>
        </div>

        <div class="form-group col-12">
          <label for="passwrod" class="form-label">Password</label>
          <input type="password" class="form-control" formControlName="password">
          <span *ngIf="!password.valid && (password.touched || userSubmitted)" class="error-block">
            <span *ngIf="password?.hasError('required')">
              Please provide password
            </span>
            <span *ngIf="password?.hasError('minlength(8)')">
              Password should not be leser then 8character
            </span>
          </span>
        </div>

        <div class="form-group col-12">
          <label for="cpassword" class="form-label">Confirm Password</label>
          <input type="password" class="form-control" formControlName="confirmPassword">
          <span *ngIf="!confirmPassword.valid && (confirmPassword.touched || userSubmitted)" class="error-block">
            <span *ngIf="confirmPassword?.hasError('required')">
              Please confirm password
            </span>
          </span>
          <span *ngIf="registrationForm.hasError('notmatched') && confirmPassword.valid">
            Password not matched
          </span>
        </div>

        <div class="form-group col-12">
          <label for="mobile" class="form-label">Mobile</label>
          <input type="text" class="form-control" formControlName="mobile">
          <span *ngIf="!mobile.valid && (mobile.touched || userSubmitted)" class="error-block">
            <span *ngIf="mobile?.hasError('required')">
              Please provide mobile number
            </span>
          </span>
        </div>
        <br>
        <div class="form-group col-12">
          <button type="submit" class="btn btn-primary">Save</button>&nbsp;
          <button type="reset" class="btn btn-secondary ml-2">Cancel</button>
        </div>
      </form>
    </div>
  </div>
  </div>
</div>
