import { Component } from '@angular/core';
import { HealthIssue } from 'src/HealthIssue';
import { OnInit, TemplateRef } from '@angular/core';
import { NavigationExtras, Router } from '@angular/router';
import { FormGroup } from '@angular/forms';

@Component({
  selector: 'app-health-issue',
  templateUrl: './health-issue.component.html',
  styleUrls: ['./health-issue.component.css']
})

export class HealthIssueComponent implements OnInit{
   highlightRow : any;
   ClickedRow:any;
   healthIssueForm!: FormGroup;
   isView!: boolean;

  constructor(private router: Router){
    this.ClickedRow = function(index: Number,isView: boolean){
      this.highlightRow = index;
      this.isView = isView;
      this.onHIDetails(this.highlightRow, this.isView);
  }
  }
  ngOnInit(): void {
  }

  onHIDetails(healthissueID: number, isView: boolean) {
    let healthIssue:HealthIssue ;
    //isView = true;
    healthIssue = this.healthissueobj.find(x => x.id === healthissueID +1 )!;//this.healthissueobj.find(hi=>hi.id = healthissueID + 1)!;
    let navigationExtras: NavigationExtras = {
      state: {
        healthIssue,
        isView
        },
      };
    this.router.navigate(['../HealthIssueDetails'],navigationExtras);
  }

  healthissueobj= [
    new HealthIssue(1,'Acute Pain','R52.0','Yes','01-03-2023','Active'),
    new HealthIssue(2,'Asthma acute','J45.909','No','01-06-2022','Inactive'),
    new HealthIssue(3,'Appendix','K38.0','Yes','01-03-2023','Resolved')
  ];

}
<!-- <kendo-treelist
    [kendoTreeListFlatBinding]= "healthissueobj"
    kendoTreeListExpandable
    idField="id"
    parentIdField="id"
>
    <kendo-treelist-column [expandable]="true" field="name" title="Name"> </kendo-treelist-column>
    <kendo-treelist-column field="id" title="id"></kendo-treelist-column>
    <kendo-treelist-column field="enteredDate" title="Date"></kendo-treelist-column>
</kendo-treelist> -->

<div id="treelist" style="border: 1px solid black;" [formGroup]="healthIssueForm">
    {{healthissueobj | json}}
    <table class="table table-bordered table-striped" style="background-color:white">
        <thead>
        <tr>
          <th>ID</th>
          <th>Health Issue</th>
          <th>Code</th>
          <th>HCC</th>
          <th>Entered Date</th>
          <th>Status</th>
        </tr>
        </thead>
        <tbody>
          <tr *ngFor="let hiobj of healthissueobj; let id = index" [class.active]="id == highlightRow">
            <td><span>{{hiobj.id}}</span></td>
            <td><span>{{hiobj.healthissue}}</span></td>
            <td><span>{{hiobj.code}}</span></td>
            <td><span>{{hiobj.hcc}}</span></td>
            <td><span>{{hiobj.enteredDate}}</span></td>
            <td><span>{{hiobj.status}}</span></td>
          <td width="22%">
            <button class="btn btn-sm btn-info" (click)="ClickedRow(id,true)">
              <i class="fa fa-search"></i>Details</button>&nbsp;
            <button  class="btn btn-sm btn-warning" (click)="ClickedRow(id,false)">
              <i class="fa fa-edit"></i>Edit</button>&nbsp;
            <button class="btn btn-sm btn-danger" >
              <i class="fa fa-remove"></i>Delete</button>
          </td>
        </tr>
        </tbody>
      </table>

</div>
